<template>
  <div class="wrap" @mousemove="over" @mouseup="drop">
    <div id="slider">
      <div class="drag_bg" :style="{'width': moveX + 'px'}"></div>
      <div :class="['drag_text',{'slide_ok': isOk}]" onselectstart="return false;" unselectable="on" 
      v-text="isOk ? '验证成功' : '按住滑块拖至最右边'"></div>
      <div :class="['handler',isOk ? 'handler_ok_bg' : 'handler_bg', {'radius-left': !isStart && !isOk}, {'radius-right': isOk}]" 
        id="handler"
        @mousedown="start"
        :style="{'left': moveX + 'px'}"></div>
    </div>
  </div>
</template>
<script>
  export default {
    mounted () {
      // 计算最大拖拽量
      var outerWidth = this.$el.querySelector('#slider').offsetWidth
      var handlerWidth = this.$el.querySelector('#handler').offsetWidth
      this.maxX = outerWidth - handlerWidth > 0 ? outerWidth - handlerWidth : 0
    },
    data () {
      return {
        isOk: false,
        startX: null,
        isStart: false,
        moveX: 0,
        maxX: 0
      }
    },
    methods: {
      start (dragEvent) {
        if (!this.isOk && !this.isStart) {
          this.startX = dragEvent.clientX
          this.isStart = true
        }
      },
      over (dragEvent) {
        if (!this.isOk && this.isStart) {
          this.moveX = dragEvent.clientX - this.startX
          if (this.moveX < 0) {
            this.moveX = 0
          }
          if (this.moveX > this.maxX) {
            this.moveX = this.maxX
          }
          if (this.moveX === this.maxX) {
            this.isOk = true
            this.isStart = false
            this.$emit('success')
          }
        }
      },
      drop (dragEvent) {
        dragEvent.preventDefault()
        if (!this.isOk && this.moveX <= this.maxX && this.isStart) {
          const that = this
          this.isStart = false
          // 返回初始位置
          const interval = setInterval(() => {
            that.moveX = that.moveX - 10
            if (that.moveX < 0) {
              that.moveX = 0
              clearInterval(interval)
            }
          }, 10)
        }
      }
    }
  }
</script>
